import React, { useEffect, useState } from "react";
import { getApi } from "./util/api";

export const Selector = () => {
  const [years, setYears] = useState([]);
  const [carMakes, setCarMakes] = useState([]);
  const [carModels, setCarModels] = useState([]);
  const [currentMake, setCurrentMake] = useState("");

  let curr = "";

  const getModels = (makeId) => {
    getApi(
      `  query getModels($makeId: ID!) {
      uvdb {
        search_uvdb_models(uvdb_make_id: $makeId ) {
          items {
            id
            name
          }
        }
      }
    }`,
      { makeId: makeId }
    ).then((data) => {
      setCarModels(data.data.uvdb.search_uvdb_models.items);
      //console.log(carModels);
    });
  };

  const handleChange = async (e) => {
    curr = e.target.value;
    // /await setCurrentMake(curr);
    await getModels(curr);
    //console.log(carModels);
  };

  useEffect(() => {
    getApi(`query {
      uvdb {
        search_uvdb_years {
          items {
            id
          }
        }
      }
    }`).then((data) => {
      setYears(data.data.uvdb.search_uvdb_years.items);
      //console.log(years);
    });

    getApi(`query {
      uvdb {
        search_uvdb_makes(limit: 534) {
          items {
            id
            name
          }
        }
      }
    }`).then((data) => {
      setCarMakes(data.data.uvdb.search_uvdb_makes.items);
    });
  }, []);

  const modelLength =
    carModels.length === 1 ? (
      <select>
        <option defaultValue hidden>
          Model
        </option>
        {carModels.map((m) => (
          <option key={m.id} value={m.id} defaultValue={m.name}>
            {m.name}
          </option>
        ))}
      </select>
    ) : (
      <select>
        <option defaultValue hidden>
          Model
        </option>
        {carModels.map((m) => (
          <option key={m.id} value={m.id}>
            {m.name}
          </option>
        ))}
      </select>
    );

  return (
    <div>
      <h1 className="mb-5 font-bold">Choose your vehicle</h1>
      <select>
        <option defaultValue hidden>
          Year
        </option>
        {years.map((y) => (
          <option key={y.id}>{y.id}</option>
        ))}
      </select>
      <select onChange={(e) => handleChange(e)}>
        <option hidden>Make</option>
        {carMakes.map((m) => (
          <option value={m.id} key={m.id}>
            {m.name}
          </option>
        ))}
      </select>
      {modelLength}
      {curr}
    </div>
  );
};
