import React, { useEffect, useState } from "react";
import { getApi } from "./util/api";
import { useData } from "./util/useData";

export const Selector = () => {
  const {
    years,
    carMakes,
    carModels,
    setCarModels,
    fetchYears,
    fetchCarMakes,
    fetchCarModels,
  } = useData();

  // /const [carModels, setCarModels] = useState([]);
  const [isSwitched, setIsSwitched] = useState(true);
  const [isYearDisabled, setIsYearDisabled] = useState(false);
  const [isMakeDisabled, setIsMakeDisabled] = useState(true);
  const [isModelDisabled, setIsModelDisabled] = useState(true);

  const changeUI = () => {
    setIsSwitched(!isSwitched);
    console.log(isSwitched);
    if (isSwitched) {
      setIsYearDisabled(true);
      setIsModelDisabled(true);
      setIsMakeDisabled(false);
      setCarModels([]);
    } else {
      setIsYearDisabled(false);
      setIsModelDisabled(true);
      setIsMakeDisabled(true);
      setCarModels([]);
    }
  };

  const changeMakeDisabled = () => {
    setIsMakeDisabled(false);
  };

  const changeModelDisabled = () => {
    setIsModelDisabled(false);
    if (!isSwitched) {
      changeYearDisabled();
    }
  };

  const changeYearDisabled = () => {
    setIsYearDisabled(false);
  };

  let curr = "";

  const getModels = (makeId) => {
    getApi(
      `  query getModels($makeId: ID!) {
      uvdb {
        search_uvdb_models(uvdb_make_id: $makeId ) {
          items {
            id
            name
          }
        }
      }
    }`,
      { makeId: makeId }
    ).then((data) => {
      setCarModels(data.data.uvdb.search_uvdb_models.items);
    });
  };

  const handleChange = async (e) => {
    curr = e.target.value;
    await getModels(curr);
    changeModelDisabled();
  };

  useEffect(() => {
    fetchYears();
    fetchCarMakes();
  }, []);

  const modelLength =
    carModels.length === 1 ? (
      <select className="w-40 border-2 border-gray-400 py-2 rounded-lg mx-5">
        <option defaultValue hidden>
          Model
        </option>
        {carModels.map((m) => (
          <option key={m.id} value={m.id} selected>
            {m.name}
          </option>
        ))}
      </select>
    ) : (
      <select
        disabled={isModelDisabled}
        className="w-40 border-2 border-gray-400 py-2 rounded-lg mx-5"
      >
        <option defaultValue hidden>
          Model
        </option>
        {carModels.map((m) => (
          <option key={m.id} value={m.id}>
            {m.name}
          </option>
        ))}
      </select>
    );

  const switchedUI = isSwitched ? (
    <div>
      <select
        onChange={changeMakeDisabled}
        className="w-40 border-2 border-gray-400 py-2 rounded-lg mx-5"
      >
        <option defaultValue hidden>
          Year
        </option>
        {years.map((y) => (
          <option key={y.id}>{y.id}</option>
        ))}
      </select>
      <select
        disabled={isMakeDisabled}
        onChange={(e) => handleChange(e)}
        className="w-40 border-2 border-gray-400 py-2 rounded-lg mx-5"
      >
        <option hidden>Make</option>
        {carMakes.map((m) => (
          <option value={m.id} key={m.id}>
            {m.name}
          </option>
        ))}
      </select>
      {modelLength}
    </div>
  ) : (
    <div>
      <select
        onChange={(e) => handleChange(e)}
        className="w-40 border-2 border-gray-400 py-2 rounded-lg mx-5"
      >
        <option hidden>Make</option>
        {carMakes.map((m) => (
          <option value={m.id} key={m.id}>
            {m.name}
          </option>
        ))}
      </select>
      {modelLength}
      <select
        disabled={isYearDisabled}
        className="w-40 border-2 border-gray-400 py-2 rounded-lg mx-5"
      >
        <option defaultValue hidden>
          Year
        </option>
        {years.map((y) => (
          <option key={y.id}>{y.id}</option>
        ))}
      </select>
    </div>
  );

  return (
    <div>
      <h1 className="flex justify-center mb-20 text-5xl">
        🔍 MMY/YMM Vehicle Selector
      </h1>
      <div className="ml-5">
        <h1 className="mb-5 font-bold text-xl">Choose your vehicle</h1>
      </div>
      <div className="flex">
        {switchedUI}
        <button
          onClick={changeUI}
          className="bg-indigo-700 text-white px-3 py-2 rounded-lg hover:bg-indigo-600 transition-all"
        >
          Switch
        </button>
      </div>
    </div>
  );
};
