import { useState } from "react";
import { getApi } from "./api";
import { useBool } from "./useBool";

export const useData = () => {
  const { changeIsModelDisabled } = useBool();
  let currentCarMake = "";
  const [years, setYears] = useState([]);
  const [carMakes, setCarMakes] = useState([]);
  const [carModels, setCarModels] = useState([]);

  const fetchYears = () => {
    getApi(`query {
      uvdb {
        search_uvdb_years {
          items {
            id
          }
        }
      }
    }`).then((data) => {
      setYears(data.data.uvdb.search_uvdb_years.items);
    });
  };

  const fetchCarMakes = () => {
    getApi(`query {
      uvdb {
        search_uvdb_makes(limit: 534) {
          items {
            id
            name
          }
        }
      }
    }`).then((data) => {
      setCarMakes(data.data.uvdb.search_uvdb_makes.items);
    });
  };

  const fetchCarModels = (makeId) => {
    getApi(
      `  query getModels($makeId: ID!) {
      uvdb {
        search_uvdb_models(uvdb_make_id: $makeId ) {
          items {
            id
            name
          }
        }
      }
    }`,
      { makeId: makeId }
    ).then((data) => {
      setCarModels(data.data.uvdb.search_uvdb_models.items);
    });
  };

  const fillCarModels = async (e) => {
    changeIsModelDisabled();
    currentCarMake = e.target.value;
    await fetchCarModels(currentCarMake);
  };

  return {
    years,
    carMakes,
    carModels,
    setCarModels,
    fetchYears,
    fetchCarMakes,
    fetchCarModels,
    fillCarModels,
  };
};
