import React, { useEffect, useState, useRef } from "react";
import { getApi } from "./util/api";

export const Selector = () => {
  const [years, setYears] = useState([]);
  const [carMakes, setCarMakes] = useState([]);

  const yearsRef = useRef(years);

  useEffect(() => {
    getApi(`query {
      uvdb {
        search_uvdb_years {
          items {
            id
          }
        }
      }
    }`).then((data) => {
      years = data.data.uvdb.search_uvdb_years.items;
      console.log(data.data.uvdb.search_uvdb_years.items);
      //setYears(item);
    });
  }, []);

  return (
    <div>
      <select>
        <option defaultValue hidden>
          Year
        </option>
        {years.map((y) => (
          <option key={y.id}>{y.id}</option>
        ))}
      </select>
      <select>
        <option defaultValue hidden>
          Make
        </option>
        {carMakes.map((m) => (
          <option value={m.id} key={m.id}>
            {m.name}
          </option>
        ))}
      </select>
      <select>
        <option defaultValue hidden>
          Model
        </option>
      </select>
    </div>
  );
};
