import React, { useEffect, useState } from "react";
import { useData } from "./util/useData";
import { useBool } from "./util/useBool";

export const Selector = () => {
  let currentCarMake = "";
  const {
    years,
    carMakes,
    carModels,
    setCarModels,
    fetchYears,
    fetchCarMakes,
    fetchCarModels,
  } = useData();
  const {
    isSwitched,
    isYearDisabled,
    isMakeDisabled,
    isModelDisabled,
    setIsYearDisabled,
    setIsMakeDisabled,
    setIsModelDisabled,
    switchUI,
    changeIsYearDisabled,
  } = useBool();

  const changeMakeDisabled = () => {
    setIsMakeDisabled(false);
  };

  const changeModelDisabled = () => {
    setIsModelDisabled(false);
    if (!isSwitched) {
      changeIsYearDisabled();
    }
  };

  const handleChange = async (e) => {
    currentCarMake = e.target.value;
    await fetchCarModels(currentCarMake);
    changeModelDisabled();
  };

  useEffect(() => {
    fetchYears();
    fetchCarMakes();
  }, [fetchYears, fetchCarMakes]);

  const modelLength =
    carModels.length === 1 ? (
      <select className="w-40 border-2 border-gray-400 py-2 rounded-lg mx-5">
        <option defaultValue hidden>
          Model
        </option>
        {carModels.map((m) => (
          <option key={m.id} value={m.id} selected>
            {m.name}
          </option>
        ))}
      </select>
    ) : (
      <select
        disabled={isModelDisabled}
        className="w-40 border-2 border-gray-400 py-2 rounded-lg mx-5"
      >
        <option defaultValue hidden>
          Model
        </option>
        {carModels.map((m) => (
          <option key={m.id} value={m.id}>
            {m.name}
          </option>
        ))}
      </select>
    );

  const switchedUI = isSwitched ? (
    <div>
      <select
        onChange={changeMakeDisabled}
        className="w-40 border-2 border-gray-400 py-2 rounded-lg mx-5"
      >
        <option defaultValue hidden>
          Year
        </option>
        {years.map((y) => (
          <option key={y.id}>{y.id}</option>
        ))}
      </select>
      <select
        disabled={isMakeDisabled}
        onChange={(e) => handleChange(e)}
        className="w-40 border-2 border-gray-400 py-2 rounded-lg mx-5"
      >
        <option hidden>Make</option>
        {carMakes.map((m) => (
          <option value={m.id} key={m.id}>
            {m.name}
          </option>
        ))}
      </select>
      {modelLength}
    </div>
  ) : (
    <div>
      <select
        onChange={(e) => handleChange(e)}
        className="w-40 border-2 border-gray-400 py-2 rounded-lg mx-5"
      >
        <option hidden>Make</option>
        {carMakes.map((m) => (
          <option value={m.id} key={m.id}>
            {m.name}
          </option>
        ))}
      </select>
      {modelLength}
      <select
        disabled={isYearDisabled}
        className="w-40 border-2 border-gray-400 py-2 rounded-lg mx-5"
      >
        <option defaultValue hidden>
          Year
        </option>
        {years.map((y) => (
          <option key={y.id}>{y.id}</option>
        ))}
      </select>
    </div>
  );

  return (
    <div>
      <h1 className="flex justify-center mb-20 text-5xl">
        🔍 MMY/YMM Vehicle Selector
      </h1>
      <div className="ml-5">
        <h1 className="mb-5 font-bold text-xl">Choose your vehicle</h1>
      </div>
      <div className="flex">
        {switchedUI}
        <button
          onClick={switchUI}
          className="bg-indigo-700 text-white px-3 py-2 rounded-lg hover:bg-indigo-600 transition-all"
        >
          Switch
        </button>
      </div>
    </div>
  );
};
