import React, { useEffect, useState } from "react";
import { getApi } from "./util/api";

export const Selector = () => {
  const [isSwitched, setIsSwitched] = useState(false);
  const [years, setYears] = useState([]);
  const [carMakes, setCarMakes] = useState([]);
  const [carModels, setCarModels] = useState([]);
  const [currentMake, setCurrentMake] = useState("");

  const handleMakeChange = (e) => {
    setCurrentMake(e.target.value);
    console.log(currentMake);
  };

  const getMakeModels = (makeId) => {
    getApi(
      `
    query getModels($makeId: ID!) {
      uvdb {
        search_uvdb_models(uvdb_make_id: $makeId) {
          items {
            id
            name
          }
        }
      }
    }`,
      { makeId: makeId }
    ).then((data) => {
      data.data.uvdb.search_uvdb_models.items.forEach((model) => {
        carModels.push(model);
      });
      console.log(carModels);
      setCarModels(carModels);
    });
  };

  const switchOrder = () => {
    setIsSwitched(!isSwitched);
  };
  useEffect(() => {
    getApi(`query {
      uvdb {
        search_uvdb_years {
          items {
            id
          }
        }
      }
    }`).then((data) => {
      data.data.uvdb.search_uvdb_years.items.forEach((year) => {
        years.push(year);
      });
      setYears(years);
    });

    getApi(`query {
      uvdb {
        search_uvdb_makes(limit: 1000) {
          items {
            id
            name
          }
        }
      }
    }`).then((data) => {
      data.data.uvdb.search_uvdb_makes.items.forEach((make) => {
        carMakes.push(make);
      });
      setCarMakes(carMakes);
    });
  }, [carMakes, years]);

  const switchedUi = !isSwitched ? (
    <div>
      <button onClick={() => getMakeModels("UMAK1491")}>Get Car</button>
      <select
        className="mt-1 mr-8 w-32 border-2 py-2 px-3 border-gray-300 rounded-xl focus:outline-none"
        value={currentMake}
        onChange={() => handleMakeChange}
      >
        <option defaultValue hidden>
          Year
        </option>
        {years.map((y) => (
          <option key={y.id} value={y.id}>
            {y.id}
          </option>
        ))}
      </select>
      <select className="mt-1 mr-8 w-32 border-2 py-2 px-3 border-gray-300 rounded-xl focus:outline-none">
        <option defaultValue hidden>
          Make
        </option>
        {carMakes.map((m) => (
          <option key={m.id}>{m.name}</option>
        ))}
      </select>
      <select className="mt-1 mr-8 w-32 border-2 py-2 px-3 border-gray-300 rounded-xl focus:outline-none">
        <option defaultValue hidden>
          Model
        </option>
        {carModels.map((m) => (
          <option key={m.id} value={m.name}>
            {m.name}
          </option>
        ))}
      </select>
      <button
        className="bg-indigo-400 py-2 px-3 rounded-lg"
        onClick={switchOrder}
      >
        Switch Order
      </button>
    </div>
  ) : (
    <div>
      <button onClick={() => getMakeModels("UMAK1491")}>Get Car</button>
      <select className="mt-1 mr-8 w-32 border-2 py-2 px-3 border-gray-300 rounded-xl focus:outline-none">
        <option defaultValue hidden>
          Make
        </option>
        {carMakes.map((m) => (
          <option key={m.id}>{m.name}</option>
        ))}
      </select>
      <select className="mt-1 mr-8 w-32 border-2 py-2 px-3 border-gray-300 rounded-xl focus:outline-none">
        <option defaultValue hidden>
          Model
        </option>
        {carModels.map((m) => (
          <option key={m.id} value={m.name}>
            {m.name}
          </option>
        ))}
      </select>
      <select className="mt-1 mr-8 w-32 border-2 py-2 px-3 border-gray-300 rounded-xl focus:outline-none">
        <option defaultValue hidden>
          Year
        </option>
        {years.map((y) => (
          <option key={y.id}>{y.id}</option>
        ))}
      </select>
      <button
        className="bg-indigo-400 py-2 px-3 rounded-lg"
        onClick={switchOrder}
      >
        Switch Order
      </button>
    </div>
  );

  return <div>{switchedUi}</div>;
};
