import React, { useEffect, useState } from "react";
import { getApi } from "./util/api";

export const Selector = () => {
  const [years, setYears] = useState([]);
  const [carMakes, setCarMakes] = useState([]);

  useEffect(() => {
    getApi(`query {
      uvdb {
        search_uvdb_years {
          items {
            id
          }
        }
      }
    }`).then((data) => {
      data.data.uvdb.search_uvdb_years.items.forEach((year) => {
        years.push(year);
      });
      console.log(years);
      setYears(years);
    });

    getApi(`query {
      uvdb {
        search_uvdb_makes(limit: 1000) {
          items {
            id
            name
          }
        }
      }
    }`);
  }, []);
  return (
    <div>
      <select>
        <option defaultValue>Year</option>
        {years.map((y) => (
          <option key={y.id}>{y.id}</option>
        ))}
      </select>
      <select>
        <option defaultValue hidden>
          Make
        </option>
      </select>
      <select>
        <option defaultValue hidden>
          Model
        </option>
      </select>
    </div>
  );
};
