import { useState } from "react";
import { getApi } from "./api";

export const useYears = () => {
  const [years, setYears] = useState([]);

  const fetchYears = () => {
    getApi(`query {
      uvdb {
        search_uvdb_years {
          items {
            id
          }
        }
      }
    }`).then((data) => {
      setYears(data.data.uvdb.search_uvdb_years.items);
    });
  };

  return {
    years,
    setYears,
    fetchYears,
  };
};
