import React, { useEffect, useState } from "react";
import { getApi } from "./util/api";

export const Selector = () => {
  const [isSwitched, setIsSwitched] = useState(false);
  const [years, setYears] = useState([]);
  useEffect(() => {
    getApi(`query {
      uvdb {
        search_uvdb_years {
          items {
            id
          }
        }
      }
    }`).then((data) => {
      data.data.uvdb.search_uvdb_years.items.forEach((year) => {
        years.push(year);
      });
      setYears(years);
    });
  }, [years]);

  const switchedUi = isSwitched ? (
    <div>
      <select className="mt-1 mr-8 w-32 border-2 py-2 px-3 border-gray-300 rounded-xl focus:outline-none">
        <option defaultValue hidden>
          Year
        </option>
        {years.map((y) => (
          <option>{y.id}</option>
        ))}
      </select>
      <select className="mt-1 mr-8 w-32 border-2 py-2 px-3 border-gray-300 rounded-xl focus:outline-none">
        <option defaultValue hidden>
          Make
        </option>
      </select>
      <select className="mt-1 mr-8 w-32 border-2 py-2 px-3 border-gray-300 rounded-xl focus:outline-none">
        <option defaultValue hidden>
          Model
        </option>
      </select>
    </div>
  ) : (
    <div>
      <select className="mt-1 mr-8 w-32 border-2 py-2 px-3 border-gray-300 rounded-xl focus:outline-none">
        <option defaultValue hidden>
          Make
        </option>
      </select>
      <select className="mt-1 mr-8 w-32 border-2 py-2 px-3 border-gray-300 rounded-xl focus:outline-none">
        <option defaultValue hidden>
          Model
        </option>
      </select>
      <select className="mt-1 mr-8 w-32 border-2 py-2 px-3 border-gray-300 rounded-xl focus:outline-none">
        <option defaultValue hidden>
          Year
        </option>
        {years.map((y) => (
          <option>{y.id}</option>
        ))}
      </select>
    </div>
  );

  return (
    <div>
      <select className="mt-1 mr-8 w-32 border-2 py-2 px-3 border-gray-300 rounded-xl focus:outline-none">
        <option defaultValue hidden>
          Year
        </option>
        {years.map((y) => (
          <option>{y.id}</option>
        ))}
      </select>
      <select className="mt-1 mr-8 w-32 border-2 py-2 px-3 border-gray-300 rounded-xl focus:outline-none">
        <option defaultValue hidden>
          Make
        </option>
      </select>
      <select className="mt-1 mr-8 w-32 border-2 py-2 px-3 border-gray-300 rounded-xl focus:outline-none">
        <option defaultValue hidden>
          Model
        </option>
      </select>
    </div>
  );
};
